import React, {useRef, useState, useEffect} from 'react';
import {StyleSheet, Text, View, TouchableOpacity} from 'react-native';
import {startService, ByronPlayer} from '@byron-react-native/dlna-player';
import {ByronEmitter, dlnaEventName} from '@byron-react-native/dlna-player';
import Slider from '@react-native-community/slider';

const url =
  'https://a13.fp.ps.netease.com/file/61c88e88ddf9cd217c702731Jmxprdis03?.mp4';

const App = () => {
  const [uri, setUri] = useState(url);
  const [duration, setDuration] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [isSliding, setIsSliding] = useState(false);
  const [paused, setPaused] = useState(false);
  const [seek, setSeek] = useState(0);
  const viewRef = useRef(null);

  useEffect(() => {
    startService('@byron-react-native/dlna-player');
    ByronEmitter.addListener(dlnaEventName, info => {
      if (info.url) {
        setUri(info.url);
      }
    });
  }, []);

  const onStart = event => {
    console.log(' >> onStart:', event);
    setDuration(event.duration);
  };
  const onError = () => {
    console.log(' >> onError');
  };
  const onBuffer = () => {
    console.log(' >> onBuffer');
  };
  const onPaused = bool => {
    console.log(' >> onPaused:', bool);
    setPaused(bool);
  };
  const onProgress = event => {
    console.log(' >> onProgress:', event);
    if (isSliding) {
      setSeek(0);
      setIsSliding(false);
    }
    setCurrentTime(event.currentTime);
  };
  const onEnd = () => {
    console.log(' >> onEnd');
  };
  const onSwitch = () => {
    console.log(' >> onSwitch');
  };
  const onSlidingComplete = val => {
    setSeek(val);
    viewRef.current?.setNativeProps({
      seek: val,
    });
  };
  const nowTime = currentTime ? getDurationTime(currentTime / 1000) : '--';
  const totalTime = duration ? getDurationTime(duration / 1000) : '--';
  const position = duration && currentTime ? currentTime / duration : 0;
  return (
    <View style={styles.container}>
      <Text style={styles.welcome}>☆RNByronDLNA example☆</Text>
      <ByronPlayer
        source={{uri}}
        onStart={onStart}
        onError={onError}
        onBuffer={onBuffer}
        onPaused={onPaused}
        onProgress={onProgress}
        onEnd={onEnd}
        onSwitch={onSwitch}
        style={{height: 240, marginVertical: 10}}
        paused={paused}
        ref={viewRef}
      />
      <View style={styles.time}>
        <Text>{totalTime}</Text>
        <Text style={{color: 'red', marginHorizontal: 10}}>/</Text>
        <Text style={{color: 'blue', width: 100}}>{nowTime}</Text>
      </View>
      <TouchableOpacity
        onPress={() => setPaused(!paused)}
        style={[styles.btn, {backgroundColor: paused ? 'red' : 'blue'}]}>
        <Text style={{color: '#fff'}}>{paused ? '已暂停' : '播放中'}</Text>
      </TouchableOpacity>
      {duration ? (
        <Slider
          minimumValue={1 / duration}
          maximumValue={1}
          value={isSliding ? seek : position}
          minimumTrackTintColor={'blue'}
          maximumTrackTintColor="grey"
          onSlidingComplete={onSlidingComplete}
          onSlidingStart={() => setIsSliding(true)}
          thumbTintColor={'red'}
          style={styles.silder}
        />
      ) : null}
    </View>
  );
};

function getDurationTime(time) {
  const h = Math.floor(time / 3600);
  const m = Math.floor((time / 60) % 60);
  const s = Math.floor(time % 60);
  const hh = h ? h + ':' : '';
  const mm = m < 10 ? '0' + m : m;
  const ss = s < 10 ? '0' + s : s;
  return `${hh}${mm}:${ss}`;
}

export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  welcome: {
    fontSize: 20,
    textAlign: 'center',
    margin: 10,
  },
  time: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 10,
  },
  silder: {
    width: 200,
    height: 30,
    marginVertical: 10,
  },
  btn: {
    width: 120,
    height: 36,
    borderRadius: 18,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginVertical: 10,
  },
});
